create table tb_approval (garbage_pickup_capacity_percentage_type_code varchar(2) not null comment '종량제 쓰레기 수집 퍼센트(01: 0%, 02: 25%, 03: 50%, 04: 75%, 05: 100%)', is_delete varchar(1) not null comment '삭제 여부(Y/N)', rubbish_bag_liter integer not null comment '종량제 리터수', approval_datetime datetime(6) not null comment '인증일시', approval_id bigint not null auto_increment comment '인증 고유번호', approval_img_file_id bigint not null comment '인증 이미지 고유번호', user_id bigint not null comment '유저 고유번호', primary key (approval_id)) engine=InnoDB;
create table tb_crew (max_count integer not null comment '크루 최대 인원', created_datetime datetime(6) not null comment '크루 생성일시', crew_id bigint not null auto_increment comment '크루 고유번호', crew_main_img_file_id bigint comment '크루 메인 이미지 고유번호', updated_datetime datetime(6) not null comment '크루 수정일시', user_id bigint not null comment '유저 마스터 고유번호', crew_name varchar(100) not null comment '크루명', crew_content text not null comment '크루 소개글', is_delete varchar(255) not null comment '크루 삭제여부(Y/N)', is_hot_crew varchar(255) not null comment '핫크루 선정 여부(Y/N)', primary key (crew_id)) engine=InnoDB;
create table tb_crew_feed (is_announcement varchar(1) not null comment '공지글 여부(Y/N)', is_delete varchar(1) not null comment '삭제 여부(Y/N)', created_datetime datetime(6) not null comment '피드 생성일시', crew_feed_id bigint not null auto_increment comment '크루 피드 고유번호', crew_id bigint not null comment '크루 고유번호', updated_datetime datetime(6) not null comment '피드 수정일시', user_id bigint not null comment '작성자 고유번호', feed_content text not null comment '피드 내용', feed_title varchar(255) not null comment '피드 제목', primary key (crew_feed_id)) engine=InnoDB;
create table tb_crew_feed_img_mapping (crew_feed_id bigint not null auto_increment comment '크루 피드 고유번호', file_id bigint comment '피드 이미지 파일 고유번호', primary key (crew_feed_id)) engine=InnoDB;
create table tb_crew_hash_tag (crew_id bigint not null auto_increment comment '크루 고유번호', hash_tag_name varchar(255) comment '해쉬태그', primary key (crew_id)) engine=InnoDB;
create table tb_file (created_datetime datetime(6) not null comment '파일 등록일시', file_id bigint not null auto_increment comment '파일 고유번호', updated_datetime datetime(6) not null comment '파일 수정일시', file_extension varchar(50) not null comment '파일 확장자', origin_name varchar(100) not null comment '유저가 작성한 파일명', save_name varchar(100) not null comment '저장된 파일명', base_url varchar(2000) not null comment '파일 저장 BaseURL', file_path varchar(2000) not null comment '파일 저장 경로', primary key (file_id)) engine=InnoDB;
create table tb_invite (created_datetime datetime(6) not null comment '발실일시', crew_id bigint not null comment '크루 고유번호', invite_id bigint not null auto_increment comment '초대 고유번호', reception_datetime datetime(6) comment '수신자 앱 진입일시', reception_user_id bigint comment '수신자 고유번호', sent_user_id bigint not null comment '발신자 고유번호', sent_url varchar(2000) not null comment '발신 URL', reception_user_fcm_token text comment '수신자 FCM 토큰', primary key (invite_id)) engine=InnoDB;
create table tb_plogging (exercise_distance integer comment '플로깅 운동 거리', is_delete varchar(1) not null comment '삭제 여부(Y/N)', plogging_status varchar(1) not null comment '플로깅 상태(I:진행중, F: 종료)', trash_liter integer comment '플로깅 수집 쓰레기 리터수', walking_count integer comment '플로깅 걸음수', work_type_code varchar(1) not null comment '플로깅 타입 코드(솔로, 크루)', approval_id bigint comment '승인 고유번호', crew_id bigint comment '크루 고유번호', end_datetime datetime(6) comment '플로깅 종료일시', plogging_id bigint not null auto_increment comment '플로깅 고유번호', start_datetime datetime(6) not null comment '플로깅 시작일시 - 시작일이 곧 작업일', walking_img_file_id bigint comment '플로깅 이동 위치 이미지', primary key (plogging_id)) engine=InnoDB;
create table tb_user (target_trash_liter integer comment '유저 목표 쓰레기 수집량', created_datetime datetime(6) not null comment '유저 생성일시', updated_datetime datetime(6) not null comment '유저 수정일시', user_id bigint not null auto_increment comment '유저 고유번호', user_img_file_id bigint comment '유저 프로필이미지', account_id varchar(255) not null comment '유저 계정 ID', fcm_token text comment '유저 FCM Token', nickname varchar(255) comment '유저 닉네', user_status_code varchar(255) not null comment '유저 상태 - N: 일반, D: 휴면, W:탈퇴', primary key (user_id)) engine=InnoDB;
create table tb_user_crew_mapping (created_datetime datetime(6) not null comment '맵핑 생성일시', is_delete varchar(1) not null comment '삭제 여부(Y/N)', crew_id bigint not null comment '크루 고유번호', updated_datetime datetime(6) not null comment '맵핑 수정일시', user_crew_mapping_id bigint not null auto_increment comment '유저 크루 맵핑 고유번호', user_id bigint not null comment '유저 고유번호', primary key (user_crew_mapping_id)) engine=InnoDB;